const initState = {}
const authReducer = (state = initState, action) => {
    switch(action.type){
        case 'LOGIN_ERROR':
            return {
                ...state,
                authError: 'login failed'
            };
        case 'LOGIN_SUCCESS': 
            return state;
    }
    return state
}
export default authReducer;
